SELECT pp.ID pp_id,pp.PROJECT_ID pp_projectId,pp.PERIOD_NAME pp_periodName,pp.PROJECT_MANAGER_ID pp_projectManagerId,pp.TOTAL_BUILDINGS pp_totalBuildings,pp.ID_TREE pp_idTree,pp.NAME_TREE pp_nameTree,pp.UPDATE_TIME pp_updateTime,pp.REMARK pp_remark,pp.TOTAL_AREA pp_totalArea, pi.ID pi_id,pi.PROJECT_NAME pi_projectName,pi.PROJECT_TYPE pi_projectType,pi.GENERAL_CONTRACT_ID pi_generalContractId,pi.CONSTRUCTION_ID pi_constructionId,pi.OWNER_UNIT pi_ownerUnit,pi.UPDATE_TIME pi_updateTime, c.ID c_id,c.CORPORATE_NAME c_corporateName,c.CORPORATE_TYPE c_corporateType,c.CORPORATE_LEADER c_corporateLeader,c.CORPORATE_CODE c_corporateCode,c.CORPORATE_LEGAL_PERSON c_corporateLegeaPerson,c.CORPORATE_ADDR c_corporateAddr,c.REMARK c_remark,c.UPDATE_TIME c_updateTime, c2.ID c2_id,c2.CORPORATE_NAME c2_corporateName,c2.CORPORATE_TYPE c2_corporateType,c2.CORPORATE_LEADER c2_corporateLeader,c2.CORPORATE_CODE c2_corporateCode,c2.CORPORATE_LEGAL_PERSON c2_corporateLegalPerson,c2.CORPORATE_ADDR c2_corporateAddr,c2.REMARK c2_remark,c2.UPDATE_TIME c2_updateTime, pb.ID pb_id,pb.PROJECT_PERIOD_ID pb_projectPeriodId,pb.ID_TREE pb_idTree,pb.NAME_TREE pb_nameTree,pb.BUILDING_NAME pb_buildingName,pb.BUILDING_SNO pb_buildingSno,pb.LOGOGRAM pb_logogram, pb.ELEVATION_ID pb_elevationId,pb.TOTAL_FLOOR pb_totalFloor,pb.HEIGHT pb_height,pb.UPDATE_TIME pb_updateTime, ph.ID ph_id,ph.ROOM_CODE ph_roomCode,ph.ROOM_NAME ph_roomName,ph.ID_TREE ph_idTree,ph.NAME_TREE ph_nameTree,ph.BUILDING_ID ph_buildingId,ph.HOUSEHOLD_CHART_ID ph_householdChartId,ph.ROOM ph_room,ph.FLOOR ph_floor,ph.REGION_TYPE_ID ph_regionTypeId,ph.UPDATE_TIME ph_updateTime
FROM ${ddBB}.PROJECT_PERIOD pp LEFT JOIN ${ddBB}.PROJECT_INFO pi ON pp.PROJECT_ID = pi.ID LEFT JOIN ${ddBB}.COMPANY c ON pi.GENERAL_CONTRACT_ID = c.ID LEFT JOIN ${ddBB}.COMPANY c2 ON pi.CONSTRUCTION_ID = c2.ID LEFT JOIN ${ddBB}.PROJECT_BUILDING pb ON pp.ID = pb.PROJECT_PERIOD_ID LEFT JOIN ${ddBB}.PROJECT_HOUSEHOLD ph ON pb.ID=ph.BUILDING_ID
WHERE pp.TENANT_ID=#{tenantId} and pp.IS_SEALED='0'
and pp.ID=#{projectPeriodId}
and ph.UPDATE_TIME > #{updateTime}
order by pb.BUILDING_SNO,ph.FLOOR,ph.ROOM

select DISTINCT ph.HOUSEHOLD_CHART_ID, uc.ID uc_id,uc.HOUSEHOLD_NAME uc_householdName,uc.HOUSEHOLD_TYPE_ID uc_householdTypeId,uc.UNIT_CHART_IMAGE_PATH uc_unitChartImagePath,uc.HOUSEHOLD_REMARK uc_householdRemark,uc.UPDATE_TIME uc_updateTime, hca.ID hca_id,hca.HOUSEHOLD_CHART_ID hca_householdChartId,hca.HOUSEHOLD_CHART_TYPE_ID hca_householdChartTypeId,hca.HOUSEHOLD_AREA_INFORMATION hca_householdAreaInformation,hca.REMARK hca_remark,hca.UPDATE_TIME hca_updateTime ,hpr.ID hpr_id,hpr.ROOM_ID hpr_roomId,hpr.HOUSEHOLD_AREA_ID hpr_houseHoldAreaId,hpr.PHOTO_NUM hpr_photoNum,hpr.REMARK hpr_remark,hpr.UPDATE_TIME hpr_updateTime FROM ${ddBB}.PROJECT_HOUSEHOLD ph JOIN ${ddBB}.UNIT_CHART uc ON ph.HOUSEHOLD_CHART_ID=uc.ID left JOIN ${ddBB}.HOUSEHOLD_CHART_AREA hca ON uc.ID=hca.HOUSEHOLD_CHART_ID left JOIN ${ddBB}.HIDDEN_PHOTOS_RECORD hpr ON hca.ID=hpr.HOUSEHOLD_AREA_ID WHERE ph.TENANT_ID=#{tenantId}
AND ph.ID_TREE LIKE '${projectPeriodId}%'
and ph.UPDATE_TIME > #{updateTime}

SELECT prpr.ID prpr_id,prpr.PROJECT_PERIOD_ID prpr_projectPeriodId,prpr.PROCEDURE_ID prpr_procedureId,prpr.REGION_TYPE_ID prpr_regionTypeId, pi.ID pi_id,pi.PROCEDURE_TYPE_ID pi_procedureTypeId,pi.PROCEDURE_NAME pi_procedureName,pi.REMARK pi_remark,pi.IS_HIDE pi_isHide,pi.UPDATE_TIME pi_updateTime, pt.ID pt_id,pt.PROCEDURE_TYPE_NAME pt_procedureTypeName,pt.PARENT_ID pt_parentId,pt.IS_LEAF pt_isLeaf,pt.ID_TREE pt_idTree,pt.NAME_TREE pt_nameTree,pt.LEVEL pt_level,pt.UPDATE_TIME pt_updateTime, di.ID di_id,di.PROCEDURE_ID di_procedureId,di.IS_GENERAL di_isGeneral,di.GRADING_STANDARD di_gradingStandard,di.ERROR_RANGE di_errorRange,di.STANDARD_VAL di_standardVal,di.UNIT di_unit,di.CALC_TYPE di_calcType,di.PASS_TEXT di_passText,di.NO_PASS_TEXT di_noPassText,di.IS_SEALED di_isSealed,di.REMARK di_remark,di.UPDATE_TIME di_updateTime, gi.ID gi_id,gi.PROCEDURE_ID gi_procedureId,gi.GRADING_STANDARD gi_gradingStandard,gi.ERROR_RANGE gi_errorRange,gi.ALLOW_MAX_VALUE gi_allowMaxValue,gi.ALLOW_MIN_VALUE gi_allowMinValue,gi.STANDARD_VAL gi_standardVal,gi.ALLOW_MAX_EXPRESSION gi_allowMaxExpression,gi.ALLOW_MIN_EXPRESSION gi_allowMinExpression ,gi.ALLOW_MAX_VAL gi_allowMaxVal,gi.CALCULATE_TYPE gi_calculateType,gi.UNIT gi_unit,gi.CALC_TYPE gi_calcType,gi.PASS_TEXT gi_passText,gi.NO_PASS_TEXT gi_noPassText,gi.IS_SEALED gi_isSealed,gi.REMARK gi_remark,gi.UPDATE_TIME gi_updateTime, tpr.ID tpr_id,tpr.PROCEDURE_ID tpr_procedureId,tpr.TEAM_ID tpr_teamId,tpr.REMARK tpr_remark,tpr.UPDATE_TIME tpr_updateTime, ti.ID ti_id,ti.TEAM_NAME ti_teamName,ti.PARTNER_ID ti_partnerId,ti.UPDATE_TIME ti_updateTime ,pi2.ID pi2_id,pi2.PARTNER_NAME pi2_partner,pi2.CORPORATE_ID pi2_corporateId,pi2.PARTNER_LEADER pi2_partnerLeader,pi2.LINKER pi2_linker,pi2.LINKER_PHONE pi2_linkerPhone,pi2.GRADE pi2_grade,pi2.IS_ENTITY pi2_isEntity ,c.ID c_id,c.CORPORATE_NAME c_corporateName,c.CORPORATE_TYPE c_corporateType,c.CORPORATE_LEADER c_corporateLeader,c.CORPORATE_CODE c_corporateCode,c.CORPORATE_LEGAL_PERSON c_corporateLegeaPerson,c.CORPORATE_ADDR c_corporateAddr,c.REMARK c_remark,c.UPDATE_TIME c_updateTime, tur.ID tur_id,tur.USER_ID tur_userId,tur.ROLE_TYPE tur_roleType,tur.TEAM_ID tur_teamId,tur.UPDATE_TIME tur_updateTime, u.ID u_id,u.USER_NAME u_userName,u.REAL_NAME u_realName
,u.PHONE u_phone,u.WECHAT u_wechat,u.TENANT_ID u_tenantId, an.ID an_id,an.PARENT_COMPANY_ID an_parentCompanyId,an.SUPERVISOR_COMPANY_ID an_supervisorCompanyId,an.CONTRACTING_PRO_ID an_contractingProId,an.CONSTRUCTION_TEAM_ID an_constructionTeamId, an.CONSTRUCTION_TEAM an_constructionTeam,an.CONSTRUCTION_MANAGER_ID an_constructionManagerId,an.PROJECT_PERIOD_ID an_perjectPeriodId, an.PROJECT_NAME an_projectName,an.REGION_ID an_regionId,an.REGION_NAME an_regionName,an.REGION_TYPE an_regionType,an.REGION_ID_TREE an_regioinIdTree, an.REGION_NAME_TREE an_regionNameTree,an.PROCEDURE_ID an_procedureId,an.PROCEDURE_NAME an_procedureName,an.STATEMENT_ID an_statementId, an.BATCH_TIMES an_batchTimes,an.CHECK_TIMES an_checkTimes,an.COINCIDENCE_RATE an_coincidenceRate,an.WORK_RATIO an_workRatio,an.SUPERVISOR_ID an_supervisorId,an.SUPERVISOR_NAME an_supervisor,an.SUPERVISOR_CHECKED an_supervisorChecked,an.SUPERVISOR_CHECK_RESULT an_supervisorCheckResult,an.SUPERVISOR_CHECK_TIME an_supervisorCheckTime,an.CONSTRUCTION_INSPECTOR an_constructionInspector,an.CONSTRUCTION_INSPECTOR_CHECKED an_constructionInspectorChecked,an.CONSTRUCTION_INSPECTOR_CHECK_RESULT an_constructionInspectorCheckResult,an.CONSTRUCTION_INSPECTOR_CHECK_DATE an_constructionInspectorCheckDate,an.TEAM_INSPECTOR an_teamInspector, an.TEAM_INSPECTOR_CHECKED an_teamInspectorChecked,an.TEAM_INSPECTOR_CHECK_RESULT an_teamInspectorCheckResult,an.TEAM_INSPECTOR_CHECK_TIME an_teamInspectorCheckTime,an.REMARK an_remark,an.UPDATE_TIME an_updateTime, ab.ID ab_id,ab.ACCEPTANCE_NOTE_ID ab_acceptanceNoteId,ab.ACCEPTANCE_NOTE_MODIFY_ID ab_acceptanceNoteModifyId,ab.ACCEPTANCE_FIRST_ID ab_acceptanceFirstId,ab.BATCH_NO ab_batchNo,ab.ELIGIBLE_RATE ab_eligibleRate,ab.ACCEPTANCE_PERSON_ID ab_acceptancePersonId,ab.ACCEPTER ab_accepter, ab.INSPECTOR_ROLE ab_inspectorRole,ab.DOMINANT_ITEM_CHECK_RESULT ab_dominantItemCheckResult,ab.GENERAL_ITEM_CHECK_RESULT ab_generalItemCheckResult,ab.MIN_PASS_RATIO ab_minPassRatio,ab.CREATE_DATE ab_createDate,ab.BATCH_STATUS_ID ab_batchStatusId,ab.UPDATE_TIME ab_updateTime, s.ID s_id,s.STATUS_NAME s_statusName,s.STATUS_NUM s_statusNum,s.NOTIFY_ORGANIZATION_TYPE s_notifyOrganizationType,s.NOTIFY_ORGANIZATION_ID s_notifyOrganizationId,s.STATUS_TYPE s_statusType,s.STATUS_TRAIT s_statusTrait,s.REMARK s_remark,s.UPDATE_TIME s_updateTime, pbs.ID pbs_id,pbs.PROCEDURE_ID pbs_procedureId,pbs.BATCH_NO pbs_batchNo,pbs.STATEMENT_ID pbs_statementId,pbs.REMARK pbs_remark,pbs.UPDATE_TIME pbs_updateTime, si.ID si_id,si.STATUS_NAME si_statusName,si.STATUS_NUM si_statusNum,si.NOTIFY_ORGANIZATION_TYPE si_notifyOrganizationType,si.NOTIFY_ORGANIZATION_ID si_notifyOrganizationId,si.STATUS_TYPE si_statusType,si.STATUS_TRAIT si_statusTrait,si.REMARK si_remark,si.UPDATE_TIME si_updateTime FROM ${ddBB}.PROJECT_REGION_PROCEDURE_RELATE prpr JOIN ${ddBB}.PROCEDURE_INFO pi on prpr.PROCEDURE_ID = pi.ID JOIN ${ddBB}.PROCEDURE_TYPE pt ON pi.PROCEDURE_TYPE_ID = pt.ID LEFT JOIN ${ddBB}.DOMINANT_ITEM di ON pi.ID = di.PROCEDURE_ID LEFT JOIN ${ddBB}.GENERAL_ITEM gi ON pi.ID = gi.PROCEDURE_ID LEFT JOIN ${ddBB}.TEAM_PROCEDURE_RELATE tpr ON pi.ID = tpr.PROCEDURE_ID LEFT JOIN ${ddBB}.PROJECT_PARTNER_RELATE ppr ON tpr.TEAM_ID=ppr.TEAM_ID and prpr.PROJECT_PERIOD_ID=ppr.PROJECT_PERIOD_ID LEFT JOIN ${ddBB}.TEAM_INFO ti ON ppr.TEAM_ID = ti.ID LEFT JOIN ${ddBB}.PARTNER_INFO pi2 ON ti.PARTNER_ID = pi2.ID LEFT JOIN ${ddBB}.COMPANY c ON pi2.CORPORATE_ID=c.ID LEFT JOIN ${ddBB}.TEAM_USER_RELATE tur ON ti.ID = tur.TEAM_ID LEFT JOIN ${ddBB}.USER u ON tur.USER_ID = u.ID LEFT JOIN ${ddBB}.ACCEPTANCE_NOTE an ON pi.ID = an.PROCEDURE_ID and prpr.REGION_TYPE_ID=an.REGION_TYPE and prpr.PROJECT_PERIOD_ID=an.PROJECT_PERIOD_ID LEFT JOIN ${ddBB}.ACCEPTANCE_BATCH ab ON an.ID = ab.ACCEPTANCE_NOTE_ID LEFT JOIN ${ddBB}.STATUS_INFO s ON ab.BATCH_STATUS_ID=s.ID LEFT JOIN ${ddBB}.PROCEDURE_BATCH_STATUS pbs ON an.ID = pbs.ACCEPTANCE_NOTE_ID LEFT JOIN ${ddBB}.STATUS_INFO si ON pbs.STATEMENT_ID = si.ID WHERE pi.TENANT_ID=#{tenantId} and pi.IS_SEALED = '0'
AND prpr.PROJECT_PERIOD_ID=#{projectPeriodId}
AND an.STATEMENT_ID NOT IN
#{status}
