mapper.select:
		SELECT DISTINCT a.* FROM ${pTableName}
		a,${prprTableName} b,${rdprTableName}
		c,${dpTableName} d,${rTableName} e,${urrTableName} f WHERE
		a.`ID`=b.`PROCEDURE_ID` AND a.`ID`=d.`DATA_ID` AND
		c.`DATA_PRIVILEGE_ID`=d.`ID` AND c.`ROLE_ID`=e.`ID` AND
		e.`ID`=f.`ROLE_ID` AND
		a.`IS_SEALED`=0 AND e.`IS_SEALED`=0 AND d.`IS_SEALED`=0 AND a.TENANT_ID=#{tenantId} AND
		b.`PROJECT_PERIOD_ID`=#{projectPeriodId} 
		AND f.`USER_ID`=#{userId} AND d.`DATA_TYPE_ID`=#{dataTypeId}

           AND b.REGION_TYPE_ID=#{regionTypeId}

		SELECT DISTINCT a.* FROM ${pTableName}
		a,${prprTableName} b
		WHERE a.`ID`=b.`PROCEDURE_ID` AND a.`IS_SEALED`=0
		AND a.TENANT_ID=#{tenantId}
		AND b.`PROJECT_PERIOD_ID`=#{projectPeriodId} 

           AND b.REGION_TYPE_ID=#{regionTypeId}
