mapper.select:
SELECT e.ID AS id,IFNULL(e.BATCH_NO,1) AS batchNo,a.NAME_TREE AS regionName,
a.${regionMinName} AS regionMinName, a.ID AS regionId,b.ID AS procedureId,
IFNULL(e.BATCH_STATUS_ID,'${batchStatusId}') AS statementId,IFNULL(e.PROJECT_PERIOD_ID,'${projectPeriodId}') as projectPeriodId,
e.ACCEPTANCE_NOTE_ID AS acceptanceNoteId,

	IFNULL(e.REGION_TYPE,'${regionType}') AS regionType,


	IFNULL(e.REGION_TYPE,a.REGION_TYPE_ID) AS regionType,

b.PROCEDURE_NAME AS procedureName, IFNULL(e.BATCH_TIMES,1) AS batchTimes,
IFNULL(e.INSPECTOR_ROLE,'${inspectorRole}') AS inspectorRole, IFNULL(e.STATEMENT_ID,'${statementId}') AS stateId 
FROM ${tableName} a LEFT JOIN ${pTableName} b ON a.`IS_SEALED`=0 LEFT JOIN (
SELECT a.ID,a.BATCH_NO,b.REGION_NAME,b.REGION_MIN_NAME,a.BATCH_STATUS_ID, a.ACCEPTANCE_NOTE_ID,
b.PROCEDURE_ID, b.BATCH_TIMES,a.INSPECTOR_ROLE,c.STATEMENT_ID,b.REGION_ID,b.PROJECT_PERIOD_ID,b.REGION_TYPE 
FROM ${abTableName} a, ${anTableName} b, ${pbsTableName} c 
WHERE a.`ACCEPTANCE_NOTE_ID`=b.`ID` AND a.`IS_SEALED`=0 AND b.`IS_SEALED`=0 AND c.`ACCEPTANCE_NOTE_ID`=b.`ID` AND
c.`BATCH_NO`=a.`BATCH_NO` AND c.`PROCEDURE_ID`=b.`PROCEDURE_ID` AND b.`PROJECT_PERIOD_ID`=#{projectPeriodId} 
AND a.BATCH_STATUS=#{batchStatus} AND a.`INSPECTOR_ROLE`=#{inspectorRole} 
) e ON e.PROCEDURE_ID=b.`ID` AND e.REGION_ID=a.`ID` WHERE 1=1 

	AND b.ID=#{procedureId} 


	AND a.`ID`=#{tid} 


	AND a.`BUILDING_ID`=#{buildingId} AND a.`FLOOR`=#{floor}

		AND a.`REGION_TYPE_ID`=#{regionType} 


ORDER BY regionMinName ASC,batchNo ASC

SELECT a.ID as id,a.BATCH_NO as batchNo,c.REGION_NAME as regionName, a.ACCEPTANCE_NOTE_ID as acceptanceNoteId, c.PROCEDURE_NAME as procedureName, c.BATCH_TIMES as batchTimes FROM ${abTableName} a,${pbsTableName} b,${anTableName} c WHERE c.`STATEMENT_ID`!=#{anStatementId} AND b.`ACCEPTANCE_NOTE_ID`=c.`ID` AND a.`ACCEPTANCE_NOTE_ID`=c.`ID` AND b.`PROCEDURE_ID`=c.`PROCEDURE_ID` AND a.`BATCH_NO`=b.`BATCH_NO` AND c.`IS_SEALED`=0 AND a.`IS_SEALED`=0 AND a.BATCH_STATUS=1 AND a.`ACCEPTANCE_PERSON_ID`=#{userId} AND a.BATCH_STATUS_ID=#{batchStatusId} AND c.PROJECT_PERIOD_ID=#{projectPeriodId} AND a.BATCH_STATUS=#{batchStatus}
AND b.`STATEMENT_ID`=#{pbsStatementId}
ORDER BY a.`UPDATE_TIME` DESC
